INSERT INTO `contacts` VALUES ('1', '', 'Баку Медикал Плаза\r\nAZ1142, Проспект Бабека 92Н Хатаинский р-н', 'Bakı ş. H. Zərdabi küçəsi, 79B', '', '', 'Onkoginekoloq Könül Mərdanova 2006 ci ildə Azərbaycan Tibb Unuversitetinin Müalicə işi fakultəsini fərqlənmə ilə bitirib. 2007 cildə Milli Onkologiya Mərkəzində Onkologiya ixtisasi üzrə internatura kursu keçib. 2009- cu ilin sentyabr ayından bu günə kimi Milli Onkologiya Mərkəzinin Şişönü xəstəliklərin diaqnostikası və müalicəsi şöbəsində həkim onkoginekoloq vəzifəsində çalışır. Bir çox elmi məqalələrin və “Vulvovaginal kandidozların mikrobioloji diaqnostikası” adlı metodik vəsaitin müəllifidir. 2020 ci ildə Azəbaycan səhiyyəsi üçün bir ilk olaraq elmi məqaləsi Akad. C. Əliyevin rəhbərliyi ilə Amerikada NCCN də dərc olunub.', 'info@konulmardanova.az', 'https://www.facebook.com/onkoginekoloqkonulmardanova/', '', '', '', '', '', '', '', '', '', 'https://www.youtube.com/channel/UCO06zpVa_iKM2AAN6YbGeFg', 'https://www.instagram.com/onkoginekolog/?igshid=9pl33ts3yezi', '+994505630189', '', '', '', '(40.37801404257197, 49.945913320539944)', 'Open Hours: Mon - Sat 9.00 - 18.00', '', '123213');

