-- ----------------------------
-- Table structure for products
-- ----------------------------
DROP TABLE IF EXISTS `products`;
CREATE TABLE `products` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `category_id` varchar(255) NOT NULL,
  `author_id` int(11) NOT NULL,
  `datetime` int(11) NOT NULL,
  `name_en` varchar(255) NOT NULL,
  `name_ru` varchar(255) NOT NULL,
  `name_az` varchar(255) NOT NULL,
  `keywords_en` varchar(255) NOT NULL,
  `keywords_ru` varchar(255) NOT NULL,
  `keywords_az` varchar(255) NOT NULL,
  `short_text_en` varchar(500) NOT NULL,
  `short_text_ru` varchar(500) NOT NULL,
  `short_text_az` text NOT NULL,
  `full_text_en` text NOT NULL,
  `full_text_ru` text NOT NULL,
  `full_text_az` text NOT NULL,
  `image` varchar(255) NOT NULL,
  `read_count` int(11) NOT NULL DEFAULT 0,
  `comment_count` int(11) NOT NULL DEFAULT 0,
  `flash` tinyint(1) NOT NULL,
  `active` tinyint(1) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `kateqoriya` (`category_id`)
) ENGINE=MyISAM AUTO_INCREMENT=62 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of products
-- ----------------------------
INSERT INTO `products` VALUES ('2', '5-9-14', '0', '1562397387', 'ADAFUL 4', 'ADAFUL 4', '', '', '', '', '', '', '', '<p><strong>ADAFUL 4</strong> - consists of vitamin-mineral complexes and this product is involved in the regulation of many metabolic processes in the body. Eliminates problems with iodine and folic acid deficiency. It improves mood, calms, eliminates fatigue. Helps normal pregnancy, is involved in the development of the nervous system of the fetus, reduces uterine contractility and convulsive contraction of peripheral muscles.</p>\r\n<p><strong>Release form:</strong> N 30 capsules</p>', '<p><strong>ADAFUL 4 -</strong> состоит из витаминно-минеральных комплексов и участвует в регуляции многих обменных процессов в организме. Устраняет проблемы с дефицитом йода и фолиевой кислоты. Улучшает настроение, успокаивает, устроняет усталость. Помогает нормальному течениию беременности, участвует в развитии нервной системы плода, уменьшает сократительную способность матки и судорожное сокрашение периферических мышц.</p>\r\n<p><strong>Форма выпуска: </strong>№30 капсула</p>', '', 'adaful-4---17-1565007627.07-1565007627.19-1565007627.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('3', '5-10', '0', '1562938964', 'ADAKİT', 'ADAKİT', '', '', '', '', '', '', '', '<p><strong>ADAKIT</strong> is a combined product. It is used to treat iron deficient anemia. It acts as a biocatalyst in the body and forms the majority of Hb and other enzymes. It helps to eliminate the iron deficiency required for the synthesis of Hb and other globulin enzymes and stimulates erythropoiesis. Fe + 2 fumarate helps to recover the amount of iron in the blood, together with Fe + 2 transferrin in the blood serum, Hb plays a role in the formation of myoglobin and helps the organism to accumulate in their stores.</p>\r\n<p><strong>Release form:</strong> 30 ml drops</p>', '<p><strong>ADAKİT</strong> комбинированное средство используется для лечения железодефицитной анемии, которая действует как биокатализатор для организмов и является основной частью Hb и других ферментов, которые помогают уменьшить дефицит железа и стимулировать эритропоз, важный для синтеза Hb и других ферментов глобулина. Fe2 + фумарат помогает восстановить количество железа в крови. В сочетании с переносом в сыворотку крови Fe + 2 участвует в образовании Hb, миоглобина и помогает организму накапливаться в депо.</p>\r\n<p><strong>Форма выпуска</strong>: 30 ml капли</p>', '', 'adakit---17-1565007588.07-1565007588.19-1565007588.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('4', '3-5-10', '0', '1562941516', 'ADAL+', 'ADAL+', '', '', '', '', '', '', '', '<p><strong>ADAL +</strong> is a combination of vitamins which regulates important metabolic processes in the body. Vitamin D contributes to the absorption of calcium from the intestines, vitamin K2 activates Matrix GLA protein, which is necessary for the absorption of bones and calcium denatat accumulated in blood and tissues. For this reason, ADAL + not only absorbs calcium from the intestines, but also delivers it to the bones. This gives more effective effect on the treatment and prevention of rickets, osteoporosis. ADAL + helps to cure clinical diseases like diabetes, rheumatic diseases, depression and so on.</p>\r\n<p><strong>Release form:</strong> 25 ml spray</p>', '<p><strong>ADAL +</strong> это комбинация витаминов каторая регулирует важные обменные процессы в организме. Витамин D способствует высасывание кальция из кишечника, витамин K2 активирует белок Matrix GLA, который необходим для поглощения костей и дентата кальция, накопленного в крови и тканях. По этой причине АДАЛ + не только поглощает кальций из кишечника, но и доставляет его в кости. Это дает более эффективное влияние на лечение и профилактику рахита, остеопороза. АДАЛ + помогает излечить клиническое заболевания как сахарной диабет, ревматические заболевания, депрессия и так далее.</p>\r\n<p><strong>Форма выпуска</strong>: 25 мл спрей</p>', '', 'adal-17-1565007556.07-1565007556.19-1565007556.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('9', '4-7', '0', '1562943472', 'ADELAS', 'ADELAS', '', '', '', '', '', '', '', '<p><strong>ADELAS</strong> has a calming, antimetrical, tocoly effect.<br />It eliminates the symptoms of nausea and vomiting during gastrointestinal, nervous diseases, and also eliminates the symptoms of tonus during pregnancy. The active ingredient of ginger extract inhibits the vomiting reflex. As part of magnesia and vitamin B6 reduces nervous tension and symptoms of neurosis.</p>\r\n<p><strong>Release form: </strong>№20 tablets</p>', '<p><strong>ADELAS</strong> обладает успокаивающим, антиметическим, токолитеским действием. <br />Он устраняет симптомы тошноты и рвоты во время желудочно-кишечных, нервных заболеваний, а также во время беременности устраняет симптомы тонуса. Действующее вещество экстракта имбиря тормозит продимость рвотного рефлекса. В составе магнезиум и витамин В6 уменьшает нервное перенапряжение и симптомы неврозности.</p>\r\n<p><strong>Форма выпуска: </strong>№ 20 таблет</p>', '', 'adelas---17-1565006932.07-1565006932.19-1565006932.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('5', '3-5-7-10', '0', '1562941961', 'ADECAL', 'ADECAL', '', '', '', '', '', '', '', '<p><strong>ADECAL</strong> is a complex remedy for rickets and bone injuries, and is also intended to restore calcium deficiency during intensive development. It consists of vitamins and minerals that will regulate ossification processes. At the same time, it eliminates the nervous activity and muscular weakness associated with hypocalcemia.</p>\r\n<p><strong>Release form</strong>: 150 ml syrup</p>', '<p><strong>АDECAL</strong> комплексное средство от рахита и травм костей, а также предназначен для восстановления дефицита кальция при интенсивном развитии. Он состоит из витаминов и минералов, которые будут регулировать процессы окостенения. В то же время устраняет нервную активность и мышечную слабость связанные с гипокальциемией.</p>\r\n<p><strong>Форма выпуска: </strong>150 мл сироп</p>', '', 'adecal-17-1565007521.07-1565007521.19-1565007521.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('6', '9-10', '0', '1562942231', 'ADEGAM', 'ADEGAM', '', '', '', '', '', '', '', '<p><strong>ADEGAM</strong> has a nootropic, sedative, neuroprotective, antiepileptic effect. It eliminates the symptoms of hyperactivity, encephalopathy in children, night sleep disorders, memory deficiency and enuresis. Improves the function of nerve cells, increases nerve conduction. Neutralizes symptoms such as neurosis, excitability and tearfulness.</p>\r\n<p><strong>Release form: </strong>120 ml syrup</p>', '<p><strong>ADEGAM</strong> - обладает ноотропным, седативным, нейропротекторным, противоэпилептическим действием.<br /> Он устраняет симптомы гиперактивности, энцефалопатии у детей, нарушения ночного сна, дефицита памяти и энуреза. Улучшает работу нервных клеток, повышает нервную проводимость. Сводит на нет такие симптомы как неврозность, возбудительность и плаксивость.</p>\r\n<p><strong>Форма выпуска</strong>: 120 мл сироп</p>', '', 'adegam-17-1565007491.07-1565007491.-1565007491.19-1565007491.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('7', '9', '0', '1562942702', 'ADEGAM PLUS', 'ADEGAM PLUS', '', '', '', '', '', '', '', '<p><strong>ADEGAM PLUS</strong> has a nootropic, cerebroprotective, angioprotective, antiaggregant and microcirculation enhancing effect. It is used to treat disorders of the central nervous system, encephalopathy, sleep disorders, epilepsy, tics, which are observed in various mental disorders. It improves the central and peripheral blood circulation, prevents hypoxia. Improves mood by increasing serotonin level.</p>\r\n<p><strong>Release form: </strong>№30 tablets</p>', '<p><strong>ADEGAM PLUS</strong> обладает ноотропным, церебропротекторным, ангиопротекторным, антиагрегантным и усиливающим микроциркуляцию эффектом. Он используется для лечения расстройств центральной нервной системы, энцефалопатии, нарушений сна, эпилепсии, тиков, энуреза, наблюдаемых при различных психических расстройствах. Улучшает центральную и периферическое кровообращение, предотвращает гипоксию. Улучшает настроение за счет повышения уровня серотонина.</p>\r\n<p><strong>Форма выпуска</strong>: №30 таблет</p>', '', 'adegam-plus---17-1565007362.07-1565007362.19-1565007362.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('8', '3-5-7-10', '0', '1562943007', 'ADEL 3', 'ADEL 3', '', '', '', '', '', '', '', '<p><strong>Adel 3</strong> contains vitamin D3 in the form of cholecalciferol, which is 25% more active than ergocalciferol. Adele 3 increases calcium absorption in the intestines and reabsorption of phosphorus in the renal canals. Normalizes the formation of the skeleton, protects the structure of bones. Calcium ions in the blood regulate skeletal muscle tone, myocardial function, transmission of nerve impulses and blood coagulation. Normalizes the function of the thyroid gland, the immune system and affects the production of lymphokines.</p>\r\n<p><strong>Release form</strong>: 15 ml drops</p>', '<p><strong>Adel 3</strong> содержит витамин Д3 в виде холекальциферола, который на 25% более активен, чем эргокальциферол. Адель 3 увеличивает всасывание кальция в кишечнике и реабсорбцию фосфора в почечных каналах. Нормализует формирование скелета, обеспечивает защиту структуры костей. Ионы кальция в крови регулируют тонус скелетных мышц, функции миокарда, передачу нервных импульсов и свертывание крови. Нормализует функцию щитовидной железы, иммунной системы и влияет на выработку лимфокинов.</p>\r\n<p><strong>Форма выпуска</strong>: 15 мл капли</p>', '', 'adel-3-17-1565007127.07-1565007127.19-1565007127.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('34', '14-17', '0', '1563780427', 'TAYT VAG 3', 'TAYT VAG 3', '', '', '', '', '', '', '', '<p><strong>TAYT VAG </strong>is a disposable shower for vaginal and external genital hygiene. Due to its unique composition, it has antiseptic, anti-inflammatory, antifungal effects and improves vaginal microflora. Tayt vag reduces burning sensation, itching and unpleasant smell. With regular use, it cleans the vagina from the secretions and restores vaginal microbiocenosis. It is ideally combined with other devices (candles, spirals, gels, etc.).</p>\r\n<p><strong>Release form</strong>: 3 vials</p>', '<p><strong>TAYT VAG </strong> одноразовый душ для гигиены влагалища и наружных половых органов. Благодаря уникальному составу обладает антисептическим, противовоспалительным, противогрибковым и улучшающим микрофлору влагалища действиями. Тайт ваг уменьшает чувства жжения, зуда и неприятный запах. При регулярном применение очищает влагалище от выделений и восстанавливает микробиоциноз вагины. Идеально сочетается с другими средствами (свечи, спирали, гели и т.д).</p>\r\n<p><strong>Форма выпуска: </strong>3 флаконы</p>', '', 'tayt-vag-3-1563782723.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('10', '4-8', '0', '1562943803', 'ADELİV', 'ADELİV', '', '', '', '', '', '', '', '<p><strong>ADENLIV</strong> has hepatoprotective, detoxification, antioxidant and membrane-protective action. It removes toxins, nitrogen compounds and salts of heavy metals from the body. Restores damaged liver cells, stimulates the synthesis of phospholipids and proteins, enhances the development of liver cells, restores liver function, prevents the formation of gall bladder and prevents the formation of stones in the gall bladder.</p>\r\n<p><strong>Release form</strong>: № 30 capsule</p>', '<p><strong>ADELİV</strong> обладает гепатопротектoрным, детоксикационном, антиоксидантным и мембранопротекторное действием. Он выводит из организма токсины, азотные соединения и соли тяжелых металлов. Восстанавливает поврежденные клетки печени, стимулирует синтез фосфолипидов и белков, усиливает развитие клеток печени, восстанавливает функцию печени, предотвращает образование желчного пузыря и предотвращает образование камней в желчном пузыре.</p>\r\n<p><strong>Форма выпуска: </strong>№30 капсулы</p>', '', 'adenliv---17-1565006903.07-1565006903.19-1565006903.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('11', '9-10', '0', '1562944205', 'ADAKARNİT', 'ADAKARNİT', '', '', '', '', '', '', '', '<p><strong>ADKARNIT </strong>- a complex product with amino acids. Participates in important metabolic processes in the body of the child, accelerates the growth of weight and height, improves psychomotor activity, improves memory and perception. It is recommended for speech disorders, mental retardation, hyperactivity, sleep disorders, lack of weight and height.</p>\r\n<p><strong>Release form</strong>:120 ml syrup</p>', '<p><strong>ADAKARNİT</strong> - комплексное средство с аминными кислотами. Участвует в важных обменных процессах в организме ребенка, ускоряет рост веса и роста, улучшает психомоторную деятельность, улучшает память и восприятие. Рекомендуется при нарушениях речи, умственной отсталости, гиперактивности, нарушениях сна, дефиците веса и роста.</p>\r\n<p><strong>Форма выпуска: </strong>120 мл сироп</p>', '', 'adkarnit-17-1565080925.07-1565080925.19-1565080925.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('12', '4-7-10', '0', '1562944542', 'ADOBİL', 'ADOBİL', '', '', '', '', '', '', '', '<p><strong>ADOBİL</strong> is a synbiotic product in drop form. Restores the microflora of the gastrointestinal tract. Due to its liquid content, microflora develops rapidly and prevents constipation, diarrhea, eliminates bloating. It is comfortable and practical to use in children under 1 year helps to relieve bloating and pain.</p>\r\n<p><strong>Release form</strong>: 10 ml drops</p>', '<p>Капли <strong>ADOBİL</strong> являtтся синбиотическим препаратом.восстанавливает микрофлору желудочно-кишечного тракта. Благодаря жидкой формы выпуска микрофлора быстро размножается и предотвращает запоры, диарею , имеет удобное и практическое применение. При применение детям до года уменьшает газообразование и симптомы колики.</p>\r\n<p><strong>Форма выпуска</strong>: 10 мл капли</p>', '', 'adobil-17-1565006022.07-1565006022.19-1565006022.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('13', '5-8-14', '0', '1563007148', 'ADOFA', '', '', '', '', '', '', '', '', '<p><strong>ADOFA</strong> regulates reproductive function. It reduces the risk of pregnancy, plays a role in restoring hormone deficiency and contributes to the healthy development of the fetus and nervous system. Provides normal spermatogenesis in men.</p>\r\n<p><strong>Release form</strong>: № 30 tablets</p>', '<p><strong>ADOFA</strong> регулирует репродуктивную функцию. Снижает риск беременности, играет роль в восстановлении гормонального дефицита и способствует здоровому развитию плода и нервной системы. Обеспечивает нормальный сперматогенез у мужчин.</p>\r\n<p><strong>Форма выпуска</strong>: № 30 таблетки</p>', '', 'adofa---17-1565005867.07-1565005867.19-1565005867.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('14', '5', '0', '1563007377', 'ADOFER', 'ADOFER', '', '', '', '', '', '', '', '<p><strong>ADOFER</strong> is an antianemic agent that prevents hemoglobin deficiency in the blood. Due to the iron in microcapsules, it is used in the treatment and prevention of iron deficiency anemia. Innovative Adofer in vegetarian capsules, easily absorbable through the gastrointestinal tract and has high efficiency. Adofer negates the following symptoms, such as headache, fatigue, loss of appetite, hair loss and stratification of nails.</p>\r\n<p><strong>Release form</strong>: №30 capsules</p>', '<p><strong>ADOFER</strong> является антианемическим средством, которое предотвращает дефицит гемоглобина в крови. За счет железа находяшегося в микрокапсулах применяется в лечении и профилактике железодефицитной анемии. Инновационный Aрасфасован в вегетарианские капсулы, легковсасывающийся через желудочно-кишечный тракт и обладает высокой эффективностью. Адофер сводит на нет следующие симптомы, такие как головная боль, усталость, потеря аппетита, выпадение волос и раслоение ногтей.</p>\r\n<p><strong>Форма выпуска</strong>: №30 капсулы</p>', '', 'adofer---17-1565005797.07-1565005797.19-1565005797.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('15', '5', '0', '1563007741', 'ADOİR-F', 'ADOİR-F', '', '', '', '', '', '', '', '<p><strong>ADOIR-F</strong> is an antianemic agent. The form of fumarate iron is more stable than other iron salts, it does not give the taste of metal in the mouth and has a high bioavailability. one of the other causes of anemia is a lack of folic acid. ADOIR-F due to optimal dose content eliminates folic acid deficiency.</p>\r\n<p><strong>Release form</strong>: №30 tablets</p>', '<p><strong>ADOİR-F</strong> является антианемическим средством. Форма фумарата железа является более стабильной, чем другие соли железа, она не дает вкус металла во рту и обладает высокой биодоступности. одна из других причин анемии, нехватка фолиевой кислоты. Адоир Ф благодаря содержании оптимальной дозы устраняет дефицит фолиевой кислоты.</p>\r\n<p><strong>Форма выпуска</strong>: №30 таблетки</p>', '', 'adoir-f-17-1565005741.07-1565005741.19-1565005741.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('16', '5-10', '0', '1563008060', 'ADOİR-K', 'ADOİR-K', '', '', '', '', '', '', '', '<p><strong>ADOİR-K</strong> - the combined agent is used to treat iron deficiency anemia, which acts as a biocatalyst for organisms and is a major part of Hb and other enzymes that help reduce iron deficiency and stimulate erythroposis, important for the synthesis of Hb and other globulin enzymes. Fe2 + fumarate helps restore the amount of iron in the blood. In combination with serum transfer, Fe + 2 participates in the formation of Hb, myoglobin and helps the body accumulate in the storage.</p>\r\n<p><strong>Release form</strong>: 150 ml syrup</p>', '<p><strong>ADOİR-K</strong> комбинированное средство используется для лечения железодефицитной анемии, которая действует как биокатализатор для организмов и является основной частью Hb и других ферментов, которые помогают уменьшить дефицит железа и стимулировать эритропоз, важный для синтеза Hb и других ферментов глобулина. Fe2 + фумарат помогает восстановить количество железа в крови. В сочетании с переносом в сыворотку крови Fe + 2 участвует в образовании Hb, миоглобина и помогает организму накапливаться в депо.</p>\r\n<p><strong>Форма выпуска</strong>: 150 мл сироп</p>', '', 'adoir-k-17-1565005700.07-1565005700.19-1565005700.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('17', '4-10-17', '0', '1563009826', 'ADOLA', 'ADOLA', '', '', '', '', '', '', '', '<p><strong>ADOLA</strong> has local anti-inflammatory, antibacterial, antispasmodic, laxative and antihelminthic effects. Due to the plants, Adola is recommended for the treatment of rectal and hemorrhoid fractures due to astringent, wound healing, antibacterial, anti-inflammatory effects.</p>\r\n<p><strong>Release form</strong>: №3 rectal oil 10 ml</p>', '<p><strong>ADOLA </strong>- ето местные противовоспалительные, антибактериальные, спазмолитические, слабительные средства. За счет растений входящий в состав Адола рекомендуется для лечения трещинах прямой кишки и геморроя из-за вяжущих, ранозаживляюших, антибактериальных, протисвоспалительных эффектов.</p>\r\n<p><strong>Форма выпуска</strong>: №3 ректальное масло 10 мл</p>', '', 'adola-17-1565005666.07-1565005666.19-1565005666.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('18', '10-17', '0', '1563009841', 'ADOLİN', 'ADOLİN', '', '', '', '', '', '', '', '<p><strong>ADOLİN</strong>, has local anti-inflammatory, antibacterial, antispasmodic, laxative and antihelminthic effects. At the expense of plants, part of it eliminates worms and their eggs.</p>\r\n<p><strong>Release form</strong>: №3 rectal oil 4,5 ml</p>', '<p><strong>ADOLİN,</strong> ето местные противовоспалительные, антибактериальные, спазмолитические, слабительные и противоглистное средства. За счет растений входящий в состав устроняет глисты и их яйца.</p>\r\n<p><strong>Форма выпуска</strong>: №3 ректальное масло 4,5 мл</p>', '', 'adolin-17-1565005621.07-1565005621.19-1565005621.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('19', '5-9-10', '0', '1563010241', 'ADOMAX', 'ADOMAX', '', '', '', '', '', '', '', '<p><strong>ADOMAX</strong> - is a comprehensive tool for indispensable nutrition, normal growth and development of the child&single_quot;s body. The presence in the composition of the necessary vitamins in the optimal dose, increase growth and provide intensive development. Adomax also eliminates mental impairment, improving perception and memory in children.</p>\r\n<p><strong>Release form</strong>: 120 syrup</p>', '<p><strong>ADOMAX</strong> - это комплексное средство для незаменимого питания, нормального роста и развития детского организма. Наличие в составе необходимых витаминов в оптимальной дозе, повышают рост и обеспечивают интенсивное развитие. Adomax также устраняет умственные нарушения, улучшая восприятие и память у детей.</p>\r\n<p><strong>Форма выпуска</strong>: 120 мл сироп</p>', '', 'adomax-17-1565005541.07-1565005541.19-1565005541.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('20', '5-7-8-10', '0', '1563010553', 'ADOMEGA', 'ADOMEGA', '', '', '', '', '', '', '', '<p><strong>ADOMEGA</strong> is a vitamin-mineral syrup with fish oil but without fish taste (tropical fruit flavor) and smell. It has metabolic, appetite and growth, resistance to infections, improving visual function and mental performance of actions. Doses of all components contained in adomeg are calculated on recommended daily doses. It is used in the treatment of chronic infectious diseases in the rehabilitation period, with hypotrophy and lack of growth, with visual acuity, with hyperactivity and mental retardation.</p>\r\n<p><strong>Release form</strong>: 150 ml syrup</p>', '<p><strong>АDOMEGA</strong>- это витаминно-минеральный сироп с рыбим жиром но без рыбного вкуса (тропический фруктовый вкус) и запаха. Он обладает метаболическим, повыщающий аппетит и рост, устойчивость к инфекциям , улучшающий зрительную функцию и умственную работаспособность действиями. Дозы всех компонентов, содержащихся в адомеге, расчитаны на рекомендованные суточные дозы.используется в лечении хронических инфекционных заболеваях в периоде реабилитации, при гипотрофии и недостаточности роста, при нарушении остроты зрения, при гиперактивности и умственной отсталости.</p>\r\n<p><strong>Форма выпуска</strong>: 150 мл сироп</p>', '', 'adomega-17-1565005493.07-1565005493.19-1565005493.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('21', '6-10', '0', '1563012092', 'ADONİN', 'ADONİN', '', '', '', '', '', '', '', '<p><strong>ADONİN</strong> nasal drops are a combination of 2 components with 3% NaCl and N-acetylcysteine. Adonin has anti-inflammatory, mucolytic, antibacterial and anti-allergic effects. Adonin is available for all age groups. Adonin reduces swelling of the mucous membrane, dilutes thick sputum and easily removes it from the nasal cavity. Unlike other nasal drops, Adonin is not addictive and can be used for a long time with allergies.</p>\r\n<p><strong>Release form</strong>: 15 ml nasal drops</p>', '<p><strong>ADONİN</strong> капли для носа представляет собой комбинацию 2-х компонентов с 3% NaCl и N-ацетилцистеином. Адонин обладает противоспалительным, муколитическим, антибактериальным и антиаллергическим действием. При использовании препарата доступны все возрастные группы. Адонин уменьшает отек слизистой оболочки, разжижает густую мокроту и легко удаляет его из полости носа. В отличие от других капель для носа, Адонин не вызывает привыкания и может использоваться в течение длительного времени при аллергиях.</p>\r\n<p><strong>Форма выпуска</strong>: 15 мл капли в нос</p>', '', 'adonin---17-1564581961.07-1564581961.19-1564581961.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('22', '6-7-10', '0', '1563013314', 'ADORPİN', 'ADORPİN', '', '', '', '', '', '', '', '<p><strong>ADORPİN</strong>- throat spray with a standardized extract of propolis, extract of mallow and chamomile. It activates immunity against infections, antibacterial effect on microbes, has an antiviral effect against viruses, locally prevents inflammation and reduces pain in the throat. Alcohol-free propolis can be used for allergic patients.</p>\r\n<p><strong>Release form</strong>: 30 ml oral spray</p>', '<p><strong>ADORPİN</strong>&nbsp;- спрей для горла со стандартизированным экстрактом прополиса, экстрактом мальвы и ромашки. Активирует иммунитет против инфекций, антибактериально действует на микробы, оказывает антивирусный эффект против вирусов, локально предотвращает воспаление и уменьшает боль в горле. Очищенный от аллергенов прополис можно применять аллергичным пациентам.</p>\r\n<p><strong>Форма выпуска</strong>: 30 мл оральный спрей</p>', '', 'adorpin---17-1564581923.07-1564581923.19-1564581923.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('23', '4-10', '0', '1563013674', 'ADORA', 'ADORA', '', '', '', '', '', '', '', '<p><strong>ADORA</strong> contributes to the natural formation of normal microflora and intestinal function, as well as create an optimal environment in the intestine for the action of digestive enzymes. In addition, they ensure the correct formation of immunity and help reduce the risk of developing infectious diseases, the possibility of manifestation of atopic dermatitis.</p>\r\n<p><strong>Release form</strong>: №10 capsules</p>', '<p><strong>ADORA</strong> способствуют естественному формированию нормальной микрофлоры и функции кишечника, а также создают оптимальную среду в кишечнике для действия пищеварительных ферментов. Кроме того, обеспечивают правильное формирование иммунитета и помогают снизить риск развития инфекционных заболеваний, возможность проявления атопического дерматита.</p>\r\n<p><strong>Форма выпуска</strong>: №10 капсулы</p>', '', 'adora---17-1564581834.07-1564581834.19-1564581834.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('24', '3', '0', '1563016653', 'ADORTA', 'ADORTA', '', '', '', '', '', '', '', '<p><strong>ADORTA</strong> is developed to restore the structure of bones and joints, as well as to increase motor function. Glucosamine and chondroitin are the main structural elements of cartilage that ensure the restoration of joints. Prevents degeneration in the joints, reduces inflammation and pain. Adotra provides bone resistance, accelerates the recovery of fractures.</p>\r\n<p><strong>Release form</strong>: №30 tablets</p>', '<p><strong>ADORTA</strong> предназначен для восстановления структуры костей и суставов, а также для увеличения двигательной функции. Глюкозамин и хондроитин являются основными структурными элементами хряща, которые обеспечивают восстановление суставов. Предотвращает дегенерацию в суставах, уменьшает на воспаление и боль. Адотра обеспечивает сопротивление костей, ускоряет восстановление переломов.</p>\r\n<p><strong>Форма выпуска</strong>: №30 таблетки</p>', '', 'adorta---17-1564581753.07-1564581753.19-1564581753.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('25', '3', '0', '1563016654', 'ADORTA GEL', 'ADORTA GEL', '', '', '', '', '', '', '', '<p><strong>ADORTA GEL</strong> l is intended for local action of restoring bone and articular structure and increasing its physical activity. Glucosamine and chondroitin are the main structural elements of cartilage that ensure the restoration of joints. It contains complex ingredients to prevent joint degeneration, has anti-inflammatory and analgesic effects. Adotra gel provides bone resistance, accelerates the recovery of fractures.</p>\r\n<p><strong>Release form</strong>: 50 gr gel</p>', '<p><strong>ADORTA ГЕЛЬ</strong> предназначен для местного действие восстановливаюший костно-суставной структуры и повышения ее двигательной активности. Глюкозамин и хондроитин являются основными структурными элементами хряща, которые обеспечивают восстановление суставов. Содержит комплексные ингредиенты для предотвращения дегенерации суставов, обладает противовоспалительным и обезболивающим действиями. Адотра гель обеспечивает сопротивление костей, ускоряет восстановление переломов.</p>\r\n<p><strong>Форма выпуска</strong>: 50 гр гель</p>', '', 'adorta-gel---17-1564581694.07-1564581694.19-1564581694.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('26', '9', '0', '1563017265', 'ADOSAL', 'ADOSAL', '', '', '', '', '', '', '', '<p><strong>ADOSAL</strong> consists of complex ingredients with a sedative, neuroprotective, antidepressant action. It is used to treat disorders of the central nervous system, encephalopathy, sleep disorders, neurosis-like states, and tics observed in various mental disorders. It improves the central nervous system and peripheral circulation, prevents hypoxia. Improves mood by increasing serotonin levels. Prevents symptoms like neurosis, excitability and tearfulness.</p>\r\n<p><strong>Release form</strong>: №20 tablets</p>', '<p><strong>ADOSAL</strong> состоит из комплексных ингредиентов с седативным, нейропротекторным, антидепрессантным действием. Применяется для лечения расстройств центральной нервной системы, энцефалопатии, нарушений сна, неврозаподобгых состаяниях, тиков, наблюдаемых при различных психических расстройствах. Улучшает центральную нервную систему и периферическое кровообращение, предотвращает гипоксию. Улучшает настроение за счет повышения уровня серотонина. Предотвращает симптомы как неврозность, возбудительность и плаксивость.</p>\r\n<p><strong>Форма выпуска</strong>: №20 таблетки</p>', '', 'adosal---17-1564581645.07-1564581645.19-1564581645.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('27', '5-7-8-14', '0', '1563017471', 'ADOVİN', 'ADOVİN', '', '', '', '', '', '', '', '<p><strong>ADOVIN</strong> is rich in vitamins, minerals, trace elements and omega-3, necessary for the normal functioning of the body. It controls the intensity of the metabolic process, increases the body&single_quot;s resistance to adverse environmental factors and boosts immunity, improves eyesight and is recommended for the prevention of cardiovascular diseases, rheumatism and skin diseases, including rehabilitation.</p>\r\n<p><strong>Release form</strong>: №40 capsules</p>', '<p><strong>ADOVİN</strong> богат витаминами, минералами, микроэлементами и омега-3, необходимыми для нормального функционирования организма. Он контролирует интенсивность обменного процесса, повышает сопротивляемость организма к неблагоприятным факторам окружающей среды и повышает иммунитет, улучшает зрение и рекомендуется для профилактики сердечно-сосудистых заболеваний, ревматизма и кожных заболеваний, включая реабилитацию.</p>\r\n<p><strong>Форма выпуска</strong>: №40 капсулы</p>', '', 'adovin---17-1564581611.07-1564581611.19-1564581611.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('28', '14-19', '0', '1563018692', 'ADUROL', 'ADURAL', '', '', '', '', '', '', '', '<p><span title=\"\"><strong>ADUROL</strong> has anti-inflammatory, uroseptic and urine-producing effects, restoring damaged mucous membranes of the urinary tract.</span> <span title=\"\">Due to its diuretic effect, it allows microbes to be washed away from the urinary tract.</span> <span title=\"\">During the destruction of bacteria in the urinary tract, the bacteria block the lectin layer, separating the mucous membranes with an anti-adhesion effect and removing them by urine.</span> <span title=\"\">It increases the resistance of the organism to infections by stimulating the immune system, normalizing the pH of the urine by dividing free radicals by antioxidant effect.</span></p>\r\n<p><span title=\"\"><strong>Release form</strong>: №30 capsules</span></p>', '<p><strong>ADUROL</strong> обладает противовоспалительным, уросептическим, мочегонным эффектами, восстанавливает поврежденную слизистую оболочку мочевыводящих путей. Благодаря мочегонному эффекту микробы вымываются из мочевыводящих путей. Устраняя бактерии из мочевых путей, блокируют слой лектина бактерий и удаляют их через мочу. Стимулирует иммунную систему для повышения устойчивости организма к инфекциям, обладает антиоксидантными свойствами, блокирует свободные радикалы, восстанавливает рН мочи.</p>\r\n<p><strong>Форма выпуска</strong>: №30 капсулы</p>', '', 'adurol---17-1564581572.07-1564581572.19-1564581572.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('29', '5-7-13', '0', '1563019130', 'ESDADA', 'ESDADA', '', '', '', '', '', '', '', '<p><strong>ESDADA</strong> is rich in vitamins, minerals, trace elements, carotenoids lutein, necessary for the normal functioning of the body. It controls the intensity of the metabolic process, activates the immune system, increases the body&single_quot;s resistance to adverse environmental factors, and the minerals contained in it provide acid-base regulators, water-salt metabolism. Restores the lost vitamin, mineral, antioxidant balance in chronic diseases, various diets.</p>\r\n<p><strong>Release form</strong>: №30 capsules</p>', '<p><strong>ESDADA</strong> богата витаминами, минералами, микроэлементами, каротиноидами лютеином, необходимыми для нормального функционирования организма. Он контролирует интенсивность обменного процесса, активизирует иммунную систему, повышает устойчивость организма к неблагоприятным факторам окружающей среды, а содержащиеся в нем минералы обеспечивают кислотно-щелочные регуляторы, водно-солевой обмен. Восстанавливает утраченный витаминный, минеральный, антиоксидантный баланс при хронических заболеваниях, различных диетах.</p>\r\n<p><strong>Форма выпуска</strong>: №30 капсулы</p>', '', 'esdada---17-1564581530.07-1564581530.19-1564581530.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('30', '14-19', '0', '1563019396', 'FORTED', 'FORTED', '', '', '', '', '', '', '', '<p><strong>FORTED</strong> combined urological agent with uroseptic, diuretic, litholytic, lithokinetic and nephroprotective action. Thanks to natural ingredients it has a strong bactericidal effect. As a result of the diuretic effect, it accelerates the removal of bacteria and clears urinary tract infections. Due to the content of potassium citrate, it peaches urine and helps remove oxalate stones.</p>\r\n<p><strong>Release form</strong>: №30 tablets</p>', '<p><strong>FORTED</strong> комбинированное урологическое средство с уросептическим, мочегонным, литолитическим, литокинетическим и нефропротекторным действием. Благодаря натуральным ингредиентам обладает сильным бактерицидным эффектом. В результате мочегонного эффекта, он ускоряет удаление бактерий и очищает от инфекций мочевыводящих путей,Благодаря содержанию цитрата калия он ошелачивает мочу и способствует удалению оксалатных камней,</p>\r\n<p><strong>Форма выпуска</strong>: №30 таблетки</p>', '', 'forted---17-1564581496.07-1564581496.19-1564581496.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('31', '9-10', '0', '1563019845', 'NEUROAD', 'NEUROAD', '', '', '', '', '', '', '', '<p><span title=\"\"><strong>NEUROAD</strong> has a combined sedative, neuroprotective, antispasmodic effect, regulates the function of the central nervous system.</span> <span title=\"\">Normalizes the process of awakening when braking.</span> <span title=\"\">Improves central and peripheral circulation.</span> <span title=\"\">Reduces the risk of dizziness, relieves hypertension, causing vascular and diuretic effects.</span> <span title=\"\">Reduces gastrointestinal spasm.</span></p>\r\n<p><span title=\"\"><strong>Release form</strong>: 30 ml drops</span></p>', '<p><strong>NEUROPAD</strong> обладает комбинированным седативным, нейропротекторным, спазмолитическим действием, регулирует функцию центральной нервной системы. Нормализует процессы пробуждения при торможении. Улучшает центральное и периферическое кровообращение. Снижает риск головокружения, снимает гипертонию, вызывая сосудистые и мочегонные действия. Уменьшает спазм желудочно-кишечного тракта.</p>\r\n<p><strong>Форма выпуска</strong>: 30 мл капли</p>', '', 'neuroad---17-1564581465.07-1564581465.19-1564581465.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('32', '3-5', '0', '1563020278', 'OSTEAD', 'OSTEAD', '', '', '', '', '', '', '', '<p><strong>OSTEAD</strong> is designed to fully form and protect bone mass. It is important for the normal functioning of many enzymatic processes, muscles, bones and the nervous system due to its minerals. In particular, Chromium improves the level of dehydroepiandrosterone and is involved in the treatment of osteoporosis, it is also boron for regulating parathyroid hormone responsible for the exchange of calcium, phosphorus, magnesium and vitamin D.<br />Ostead also strengthens tendons, provides normal protein synthesis and collagen formation.</p>\r\n<p><strong>Release form</strong>: №30 tablets</p>', '<p><strong>OSTEAD</strong> предназначен для полного формирования и защиты костной массы. Он важен для нормального функционирования многих ферментативных процессов, мышц, костей и нервной системы благодаря своим минералам. В частности, хрому улучшающий уровень дегидроэпиандростерона и участвующему в лечении остеопороза, он также бору для регулирования паратгормона отвечающий за обмен кальция, фосфора, магния и витамина D. <br />Оstead также укрепляет сухожилия, обеспечивает нормальный синтез белка и образование коллагена.</p>\r\n<p><strong>Форма выпуска</strong>: №30 таблетки</p>', '', 'ostead---17-1564581389.07-1564581389.19-1564581389.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('33', '14-15-17', '0', '1563020920', 'TAYT PLUS', 'TAYT PLUS', '', '', '', '', '', '', '', '<p>Thanks to the plants contained in <strong>TAYT PLUS,</strong> it provides antiseptic, anti-edema, wound healing and antimicrobial effect in the vaginal area. Antimicrobial, anti-fungal and anti-virus effect in the presence of many plants by increasing the synergism, allows high effects during the use of vaginal tightening effects were observed.</p>\r\n<p><strong>Release form</strong>: 50 ml gel</p>', '<p>Благодаря растениям входящие в состав <strong>TAYT PLUS</strong> оказывает местное противоспалительное, противотековое, улучшающий регенерацию, антисептическое и противомикробное действие во влагалище. Наличие в составе одномоментно нескольких растений обладающие антибактериальным, противогрибковым, противовирусным действиями позволяет получить высокий синергистический эффект При применении наблюдается также сужение влагалища.</p>\r\n<p><strong>Форма выпуска</strong>: 50 мл гель</p>', '', 'tayt-plus---17-1564581280.07-1564581280.19-1564581280.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('35', '14-17', '0', '1563782753', 'TAYT VAG', 'TAYT VAG', '', '', '', '', '', '', '', '<p><strong>TAYT VAG </strong>is a disposable shower for vaginal and external genital hygiene. Due to its unique composition, it has antiseptic, anti-inflammatory, antifungal effects and improves vaginal microflora. Tayt vag reduces burning sensation, itching and unpleasant smell. With regular use, it cleans the vagina from the secretions and restores vaginal microbiocenosis. It is ideally combined with other devices (candles, spirals, gels, etc.).</p>\r\n<p><strong>Release form</strong>: 5 vials</p>', '<p><strong>TAYT VAG </strong>одноразовый душ для гигиены влагалища и наружных половых органов. Благодаря уникальному составу обладает антисептическим, противовоспалительным, противогрибковым и улучшающим микрофлору влагалища действиями. Тайт ваг уменьшает чувства жжения, зуда и неприятный запах. При регулярном применение очищает влагалище от выделений и восстанавливает микробиоциноз вагины. Идеально сочетается с другими средствами (свечи, спирали, гели и т.д).</p>\r\n<p><span><strong>Форма выпуска</strong>: 5 </span>флаконы</p>', '', 'tayt-vag-5-1563782888.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('36', '6-10', '0', '1565091850', 'TION 3', 'TİON 3', '', '', '', '', '', '', '', '<p><strong>TION 3 </strong> is using as a medical device in nasal cavity sanitation. The use of nasal aspirator is recommended for the baby to breathe comfortably before going to bed, especially with the time interval you consume before eating. <strong>Tion 3</strong> nasal aspirator is designed to conveniently remove nasal excretion from the nose. <span title=\"\">Tion 3 is present in the filter that makes the nasal aspirator hygienic.</span> <span title=\"\">Thanks to the ultra-soft tip, Tion 3 nasal aspirator cleans the baby&single_quot;s nose without causing it.The specificity of the Tion 3 aspirant is that it has three additional ends with the aspirator.These disposable tips are available as a 3-pack.The removal of the mucus from nose also helps prevent some complications. Tion 3 nasal aspirator is used after physiological, hypertonic, or ocean water (Oretal) solutions. These solutions facilitate the removal of nasal secretions from the baby&single_quot;s nose. It should be used under control on newborns. For hygienic reasons, aspirator&single_quot;s tips are single-use.</span></p>\r\n<p><span title=\"\"><br /></span></p>\r\n<p><strong>Release form</strong>: Nasal aspirator with 3 soft tips</p>', '<p>Используется в качестве медицинского оборудования для санации полости носаспользование аспиратора для носа рекомендуется в течение промежутка времени, который вы считаете подходящим перед едой, особенно перед сном, чтобы ребенок мог дышать с комфортом. Носовой аспиратор <strong>TİON 3</strong> предназначен для легкого очищения носа от носа. Носовой аспиратор TİON 3 имеет гигиенический фильтр. Благодаря ультрамягким насадкам, назальный аспиратор TİON 3 очищает нос ребенка. Особенность аспиратора TİON 3 состоит в том, что у аспиратора есть три дополнительных конца. Эти одинарные концы доступны в виде 3-х упаковок. Получение носового прохода из носа также помогает предотвратить некоторые осложнения. Носовой аспиратор TİON 3 используется после физиологических, гипертонических или океанических водных растворов (Oretal). Эти решения позволяют легко удалить носовой ход из носа ребенка. Гигиенически, советы аспиратора были использованы в одиночку.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Форма выпуска</strong>: Носовой аспиратор с 3 мягкими наконечниками</p>', '', 'tion-3-1565091923.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('39', '6', '0', '1565186760', 'ESOL', 'ESOL', '', '', '', '', '', '', '', '<p><strong>ESOL</strong> is a composition that maintains a normal balance between the nasal cavity and sinuses. ESOL cleanses the nasal cavity from mucus. Washing the nose with Esol&nbsp; is an effective treatment and prevention of the development of acute respiratory diseases. Esol is effective for washing the nose at home for both adults and children at any age, it is allowed during pregnancy. Eliminates edema, hyperemia of the nasal cavity, improves nasal breathing. Its also developed for nasal hygiene.</p>\r\n<p><strong>Release form</strong>: 20 pcs solution mix packages</p>', '<p><strong>ESOL</strong> - это состав, поддерживающий нормальный пн баланс&nbsp; полости носа и пазух. ESOL очищает носовую полость от слизи. Промывание носа Esol содержающим раствором, является эффективным лечением и профилактикой развития острых респираторных заболеваний. Esol эффективен для промывания носа в домашних условиях и для взрослых, и для детей в любом возрасте, разрешен во время беременности. Устраняет отек, гиперемию носовой полости, улучшает носовой дыхание. Также предназначен для гигиены носовой полости.</p>\r\n<p><strong>Форма выпуска</strong>: пакеты для приготовления раствора по 20 штук</p>', '', 'esol-1565186828.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('37', '6', '0', '1565098613', 'VELORAL', 'VELORAL', '', '', '', '', '', '', '', '<p><strong>VELORAL</strong> is using for nasal and sinus cavity cleaning as a medical remedy.<br />How to use:<br />Step 1: Open the bottle of Veloral 240 ml and add water (warmish or room temperature) to the specified point.<br />Step 2: Add the powder from the Veloral pack to the bottle filled with water. A package should be added to 240 ml of water. Close the lid cover firmly. Hold the hole in the cover with your hand.<br />Step 3: Bring your head straight to the front, keep your mouth open, and then maximize the hole in the lid cover to your nose.<br />Step 4: Hand-squeeze the plastic vial. You will then see the flow of water through the other nostrils. You can perform this procedure several times in your nostril. You can exhale and hold while performing the operation. To complete the process, &frac14; or &frac12; of the vial should be emptied.<br />Step 5: Steps 3 and 4 should be applied to the other nostrils.<br />Step 6: After the procedure is completed, quickly exhale the residual waste in the nasal cavity and do not reuse the residual solution in the vial. The procedure should be done once a day.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Release form</strong>: 5 packets +240 ml vial</p>', '<p><strong>VELORAL</strong> используется для очистки полости носа и пазухи в качестве лечебного средства.<br />Как пользоваться:<br />Шаг 1: Откройте флакон Veloral на 240 мл и добавьте воду (теплой или комнатной температуры) до указанной точки.<br />Шаг 2: Добавьте порошок из пакета Veloral в бутылку с водой. В пакет следует добавить до 240 мл воды. Плотно закройте крышку. Держите отверстие в крышке рукой.<br />Шаг 3: Подведите голову прямо вперед, держите рот открытым, а затем увеличьте отверстие в крышке до носа.<br />Шаг 4: Сожмите вручную пластиковый флакон. Затем вы увидите поток воды через другие ноздри. Вы можете выполнить эту процедуру несколько раз в ноздрю. Вы можете выдохнуть и удерживать во время выполнения операции. Для завершения процесса, &frac14; или &frac12; флакона должны быть опорожнены.<br />Шаг 5: Шаги 3 и 4 должны быть применены к другим ноздрям.<br />Шаг 6: После завершения процедуры быстро выдохните остаточные отходы в полости носа и не используйте повторно остаточный раствор во флаконе. Процедура должна проводиться один раз в день.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Форма выпуска</strong>: 5 пакетиков + 240 мл флакон</p>', '', 'veloral-1565098593.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('38', '6-10', '0', '1565167465', 'VELORAL KIDS', 'VELORAL KIDS', '', '', '', '', '', '', '', '<p><strong>VELORAL KIDS</strong> is using for nasal and sinus cavity cleaning as a medical remedy.<br />How to use:<br />Step 1: Open the bottle of Veloral 120 ml and add water (warmish or room temperature) to the specified point.<br />Step 2: Add the powder from the Veloral kids pack to the bottle filled with water. A package should be added to 120 ml of water. Close the lid cover firmly. Hold the hole in the cover with your hand.<br />Step 3: Bring your head straight to the front, keep your mouth open, and then maximize the hole in the lid cover to your nose.<br />Step 4: Hand-squeeze the plastic vial. You will then see the flow of water through the other nostrils. You can perform this procedure several times in your nostril. You can exhale and hold while performing the operation. To complete the process, &frac14; or &frac12; of the vial should be emptied.<br />Step 5: Steps 3 and 4 should be applied to the other nostrils.<br />Step 6: After the procedure is completed, quickly exhale the residual waste in the nasal cavity and do not reuse the residual solution in the vial. The procedure should be done once a day.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Release form</strong>: 5 packets +120 ml vial</p>', '<p><strong>VELORAL KIDS</strong> используется для очистки полости носа и пазухи в качестве лечебного средства.<br />Как пользоваться:<br />Шаг 1: Откройте флакон Veloral kids на 120 мл и добавьте воду (теплой или комнатной температуры) до указанной точки.<br />Шаг 2: Добавьте порошок из пакета Veloral в бутылку с водой. В пакет следует добавить до 120 мл воды. Плотно закройте крышку. Держите отверстие в крышке рукой.<br />Шаг 3: Подведите голову прямо вперед, держите рот открытым, а затем увеличьте отверстие в крышке до носа.<br />Шаг 4: Сожмите вручную пластиковый флакон. Затем вы увидите поток воды через другие ноздри. Вы можете выполнить эту процедуру несколько раз в ноздрю. Вы можете выдохнуть и удерживать во время выполнения операции. Для завершения процесса, &frac14; или &frac12; флакона должны быть опорожнены.<br />Шаг 5: Шаги 3 и 4 должны быть применены к другим ноздрям.<br />Шаг 6: После завершения процедуры быстро выдохните остаточные отходы в полости носа и не используйте повторно остаточный раствор во флаконе. Процедура должна проводиться один раз в день.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Форма выпуска</strong>: 5 пакетиков + 120 мл флакон</p>', '', 'veloral-kids-1565168038.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('40', '6-10', '0', '1565264412', 'ESOL KIDS', 'ESOL KIDS', '', '', '', '', '', '', '', '<p><strong>ESOL KIDS&nbsp;</strong>is a composition that maintains a normal balance between the nasal cavity and sinuses. Esol kids cleanses the nasal cavity from mucus. Washing the nose with Esol kids is an effective treatment and prevention of the development of acute respiratory diseases. Esol kids is effective for washing the nose at home for both adults and children at any age, it is allowed during pregnancy. Eliminates edema, hyperemia of the nasal cavity, improves nasal breathing. Its also developed for nasal hygiene.</p>\r\n<p><strong>Release form</strong>: 20 pcs solution mix packages</p>', '<p><strong>ESOL&nbsp;</strong>- это состав, поддерживающий нормальный пн баланс полости носа и пазух. Esol kids очищает носовую полость от слизи. Промывание носа Esol kids содержающим раствором, является эффективным лечением и профилактикой развития острых респираторных заболеваний. Esol kids эффективен для промывания носа в домашних условиях и для взрослых, и для детей в любом возрасте, разрешен во время беременности. Устраняет отек, гиперемию носовой полости, улучшает носовой дыхание. Также предназначен для гигиены носовой полости.</p>\r\n<p><strong>Форма выпуска</strong>: пакеты для приготовления раствора по 20 штук</p>', '', 'esol-kids-1565265552.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('41', '6', '0', '1565265818', 'ORETAL', 'ORETAL', '', '', '', '', '', '', '', '<p><strong>ORETAL</strong> -&nbsp;is an ocean water for nose cleaning. Ocean water is a natural remedy for the prevention and treatment of rhinitis. It is rich in minerals, moisturizes the nasal mucosa, accelerates the recovery process, helps eliminate swelling and inflammation, and improves the function of small capillaries. It also has antiseptic properties.</p>\r\n<p><strong>Release form</strong>:&nbsp;5ml x 10 pcs one dose vials</p>', '<p><strong>ORETAL</strong> - это oкеаническая вода для чистки носа. Океаническая вода является естественным средством для профилактики и лечения насморка. Он богат минералами, увлажняет слизистую оболочку носа, ускоряет процесс выздоровления, помогает устранить отеки и воспаления, а также улучшает функцию мелких капилляров. Он также обладает антисептическими свойствами.</p>\r\n<p><strong>Форма выпуска</strong>: 5 мл х 10 флаконов по одной дозе</p>', '', 'oretal-1565267138.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('42', '3', '0', '1565269799', 'ADEKSOL', 'ADEKSOL', '', '', '', '', '', '', '', '<p><strong>Aerosol foam ADEKSOL</strong> has anti-inflammatory effect, improves microcirculation, has local anesthetic and analgesic effects. It is easy to use, it is absorbed immediately and does not leave a mark and oil. Adeksol helps fight arthritis, arthrosis and rheumatism, relieves pain during inflammation of joints, muscles, restores mobility after trauma, skin and tissue regeneration, stimulates blood supply,relieves muscle tension in sedentary lifestyles.</p>\r\n<p><strong>Release form</strong>: 150 ml aerosol foam</p>', '<p><strong>Аэрозольная пена&nbsp;ADEKSOL</strong>&nbsp;обладает противовоспалительным эффектом, улучшает микроциркуляцию, оказывает местное анестезирующее и обезболивающее действие. Благодаря пенистой форме&nbsp;Адексол помогает бороться с артритом, артрозом и ревматизмом, снимает боль при воспалениях суставов, мышц, восстанавливает подвижность после травм, регенерацию кожи и тканей, стимулирует кровоснабжение, снимает мышечное напряжение при малоподвижном образе жизни.</p>\r\n<p><strong>Форма выпуск</strong>а: 150 мл аэрозольной пены</p>', '', 'adeksol-aerosol-foam-150-ml-700x700-1565271059.png', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('43', '11', '0', '1566812529', 'APLECON', 'APLECON', '', '', '', '', '', '', '', '<p><strong>APLECON</strong> is a gentle cream for nipples. It moisturizes and soothes sensitive nipples especially for pregnant and lactating women. It is advisable to use after each shower to reduce tension during pregnancy. The cream is soothing, softening, antiseptic, enhancing the elasticity and healing properties of olive oil and lanol. Free of flavor, odorless &amp; colorless safe for both baby and mother. During breastfeeding, the cream should be used after cleansing the breast with mother milk.</p>\r\n<p><strong>Release form</strong>: 30 gr cream</p>', '<p><strong>APLECON</strong> - это нежный крем для сосков. Увлажняет и успокаивает чувствительные соски, особенно для беременных и кормящих женщин. Рекомендуется использовать после каждого душа, чтобы уменьшить напряжение во время беременности. Крем успокаивающий, смягчающий, антисептический, усиливающий эластичность и целебные свойства оливкового масла и ланола. Без аромата, без запаха и цвета безопасен для малыша и матери. Во время грудного вскармливания крем следует использовать после очищения груди с помощью материнского молока.</p>\r\n<p><strong>Форма выпуска</strong>: 30 г крема</p>', '', 'aplecon-1566812541.jpg', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('44', '14', '0', '1566818520', 'AUR FEMIGAR', 'AUR FEMIGAR', '', '', '', '', '', '', '', '<p><strong>AUR FEMIGAR</strong> disposable combination vaginal shower is designed for personal hygiene. It has antiseptic, anti-inflammatory, decongestant, antifungal, wound healing effects. The solution contains 1.25% acetic acid, which can adjust the pH of the vagina from 3 to 5. Thanks to the appropriate pH and acetic acid, it helps to treat &ldquo;bacterial vaginitis&rdquo; and &ldquo;cervical erosion&rdquo;. During bacterial vaginitis, conditionally pathogenic microflora turns into pathogenic. It contains tea tree oil, which acts as an antiseptic against pathogenic bacteria. Aur femigar contains Aloe Vera to reduce burning and itching.</p>\r\n<p><strong>Release form</strong>: 100 ml disposable vaginal douche</p>', '<p><strong>AUR FEMIGAR </strong>- Одноразовый комбинированный вагинальный душ Aur Femigar предназначен для личной гигиены. Обладает антисептическим, противовоспалительным, противоотечным, антифунгальным, ранозаживляющим действиями. Раствор содержит 1,25% уксусную кислоту, которая может регулировать рН влагалища от 3 до 5. Благодаря соответствующему pH и уксусной кислоте, помогает лечить &laquo;бактериальный вагинит&raquo; и &laquo;эрозию шейки матки&raquo;. Во время бактериального вагинита условно-патогенная микрофлора превращается в патогенную. В составе имеется масло чайного дерева, который действует как антисептик против патогенных бактерий. Aur Femigar содержит Алоэ Веру для уменьшения жжения и зуда.</p>\r\n<p><strong>Форма выпуска</strong>: 100 мл одноразовый вагинальный душ</p>', '', 'aur-femigar-1566818557.jpg', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('45', '14', '0', '1566819136', 'AUR FOAM', 'AUR FOAM', '', '', '', '', '', '', '', '<p><strong>AUR FOAM - </strong>daily cleansing of the genitalia with a soap can cause dysbiosis. For these purposes, we offer intimate Aurfoam soap. It has is an antiseptic product specially designed for soft cleansing of external genitalia. Its active ingredient is Hexamidine Dezetionate, which is very effective against bacteria, fungi and unpleasant odors. The soft consistency and specially selected formulation correspond to the natural structure of the pH of the genital organs and do not cause dysbiosis when purified. Gives freshness, cleanliness and comfort when used throughout the day. Suitable for frequent and long-term use.</p>\r\n<p><strong>Release form</strong> : 200 ml foam</p>', '<p><strong>AUR FOAM</strong> - Ежедневная очистка обычным мылом наружных половых органов приводит к дизбактериозу. Для таких целей предлагаем пену Аур фоам для гениталий . Это антисептический продукт, специально разработанный для бережной очистки наружных половых органов. Её активным компотентом является Гексамидин Дезетионат очень эффективен против бактерий, грибков и неприятных запахов. Мягкая консистенция и специально подобранная формула совместима с естественной структурой pH в области гениталий, при очистке которой не вызывает дисбиоз. При использовании в течение дня поддерживает свежесть, ощущение чистоты и комфорт. Подходит для частого и длительного использования.</p>\r\n<p><strong>Форма выпуска</strong>: 200 мл пены</p>', '', 'aurfoam-1566820372.jpg', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('46', '14', '0', '1566820475', 'AUR LACT', 'AUR LACT', '', '', '', '', '', '', '', '<p><strong>AUR LACT</strong> is daily cream for the genital area, it has delicate and soft consistency with a special formula, rich in aloe vera extract, tea tree oil and lactic acid. Lactic acid restores the vaginal microflora and supports its pH in the range from 3.8 to 4.5. Tea tree oil has an antiseptic effect and reduces irritation and burning caused by itching and dryness in the genital area. Aloe vera has anti-inflammatory and antiseptic effects. Thanks to this composition, it is used to protection of external genital area.</p>\r\n<p><strong>Release form</strong>: 100 ml vaginal cream</p>', '<p><strong>AUR LACT</strong> ето ежедневный крем для генитальной области, крем нежной и мягкой консистенции с особой формулой, богатый экстрактом алоэ веры, маслом чайного дерева и молочной кислотой. Молочная кислота восстанавливает микрофлору влагалища и поддерживает ее Рн в пределах от 3,8 до 4,5. Масло чайного дерева обладает антисептическим эффектом и уменьшает раздражение и жжение, вызванные зудом и сухостью в области половых органов. Алоэ вера обладает противовоспалительным и антисептическим действиями. Благодаря такому составу используется для защиты области наружных половых</p>\r\n<p><strong>Форма выпуск</strong><span><strong>а</strong>: 100 мл вагинальный крем</span></p>', '', 'aurlact-1566821448.jpg', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('47', '14', '0', '1566821575', 'AUR SEDA', 'AUR SEDA', '', '', '', '', '', '', '', '<p><strong>AUR SEDA</strong> is a genital cleansing soap. It contains sodium bicarbonate, which has a thinning effect. If a woman has thick discharge, it is ideal for them. Aur Seda was developed by specialists for cleaning the female genital organs from fungal and bacterial vaginitis. Aur Seda protects the genitals from germs causing inflammatory processes.</p>\r\n<p><strong>Release form</strong>: 100 ml disposable vaginal douche</p>', '<p><strong>AUR SEDA</strong> это очищающее мыло для половых органов. Он в составе имеет натрий бикарбонат, обладающий разжижающим действием. Если у женщины имеются густые выделения, он им идеально подходит. Aur seda разработана специалистами для чистки женских половых органов при грибковом и бактериальном вагините. Aur seda защищает половые органы от микробов вызывающие воспалительные процессы.</p>\r\n<p><strong>Форма выпуск</strong><span><strong>а</strong>: 100 мл одноразовый вагинальный душ</span></p>', '', 'aur-sedaa-1566822186.jpg', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('48', '14', '0', '1566822283', 'AUR TİGHT', 'AUR TİGHT', '', '', '', '', '', '', '', '<p><strong>AUR TİGHT</strong> is a disposable vaginal shower with antiseptic, disinfectant, bactericidal action. With regular use, it reduces the vaginal lumen, because potassium sulfate is present in the composition. It contains a component called aluminum sulfate, which helps women with enuresis (urinary incontinence) after multiple births. It also contains potassium sulfate, with regular use of which improves sexual quality and orgasm. It has a bactericidal and disinfecting effect due to the content of benzalkonium chloride in the composition. Benzalkonium chloride also has antifungal (Candida albicans), antiprotozoal (Chlamydia, Trichomonas vaginalis) and local contraceptive (spermatocidal) actions; inactivates viruses that cause Herpes Simplex. This shower protects a woman from sexually transmitted diseases.</p>\r\n<p><strong>Release form</strong>: 100 ml disposable vaginal douche</p>', '<p><strong>AUR TİGHT - </strong>Это одноразовый душ для влагалища, обладающий антисептическим, дезинфицирующим, бактерицидным действиями. При регулярном использовании уменьшает просвет влагалища, потому что в составе имеется сульфат калия. В составе есть компонент под названием сульфат алюминия, который помогает женщинам страдающим энурезом (недержанием мочи) после многоплодных родов. Также содержит сульфат калия, при регулярном использовании которого улучщается сексуальное качество и оргазм. Бактерицидное и дезинфицирующее действие оказывает из-за содержания в составе бензалкония хлорида. Бензалконий хлорид обладает также противогрибковым (Candida albicans), антипротозойным (Chlamydia ,Trichomonas vaginalis) и местное контрацептивным (сперматоцидное) действиями; инактивирует вирусы, вызывающие простой герпес (Herpes simplex). Этот душ защищает женщину от венерических болезней.</p>\r\n<p><strong>Форма выпуска</strong>: 100 мл одноразовый вагинальный душ</p>', '', 'aurtight-1566824104.jpg', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('56', '10', '0', '1567691772', 'BABY MERRY', 'BABY MERRY', '', '', '', '', '', '', '', '<p><strong>BABY MERRY</strong> is a product containing a combination of trace elements in the form of sodium and potassium chloride, trisodium citrate. Baby Merry has a detoxifying, restoring acid-base balance, disturbed due to the loss of electrolytes during vomiting, diarrhea, poisoning, and excessive sweating. Due to its unique composition, metabolic acidosis is correcting.</p>\r\n<p><strong>Release form</strong>: 150 ml oral solution</p>', '<p><strong>BABY MERRY</strong> это средство содержащее комбинацию микроэлементов в виде натрий и калий хлорида, тринатрий цитрата. Baby Merry обладает детоксикационным, восстановливающим кислотно-щелочную равновесию, нарушенная, вследствие потери электролитов при рвоте, диарее, отравлениях, обильном потоотделении. Из-за содержания уникального состава способствует коррекцию метаболического ацидоза.</p>\r\n<p><strong>Форма выпуска</strong>: 150 мл пероральный раствор</p>', '', 'baby-merry-1567692192.jpg', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('49', '14', '0', '1566824122', 'AUR VİRA', 'AUR VİRA', '', '', '', '', '', '', '', '<p><strong>AUR VIRA - </strong>daily genital Cleansing soap gently cleanses the external genital area while moisturizing. It is effective against bad odors caused by bacteria. Specially developed formula with rose extract for a long time leaves a feeling of freshness, does not dry the delicate skin of the genitals. The soap is designed for daily hygiene of the intimate area, and therefore compatible with the natural pH in the genital area.</p>\r\n<p><strong>Release form</strong>: 125 gr of a cleaning soap</p>', '<p>Ежедневное очищающее мыло <strong>AUR VİRA</strong> для гениталий деликатно очищает внешнюю область гениталий, одновременно увлажняя. Это эффективно против неприятных запахов, вызванных бактериями. Специально разработанная формула с экстрактом розы длительное время оставляет ощущения свежести, не сушит нежную кожу гениталий. Мыло расчитано для ежедневной гигиены интимной зоны, и поэтому совместима с естественной pH в области гениталий.</p>\r\n<p><strong>Форма выпуска</strong>: 125 гр чистящего мыла</p>', '', 'aurvira-1566824616.jpg', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('50', '11', '0', '1566824655', 'BELOVIN', 'BELOVIN', '', '', '', '', '', '', '', '<p><strong>BELOVIN</strong> is a firming cream against stretch marks. When used regularly, it helps to reduce the appearance of cracks in the skin and helps prevent the appearance of new stretch marks. Effective for cracks caused by pregnancy and weight gain. Belovine contains special oils such as almond oil, olive oil extracts and aloe vera to keep your skin soft and supple. These oils contain a lot of linoleic and oleic acids, vitamins A, F, E and B2, magnesium, phosphorus, zinc, carotenes and many other useful substances that nourish and strengthen the skin.</p>\r\n<p><strong>Release form</strong>: 30 gr cream</p>', '<p><strong>BELOVIN</strong> это укрепляющий крем против растяжек . При регулярном использовании помогает уменьшить появление трещин на коже и помогает предотвратить появление новых растяжек. Эффективен при трещинах, вызванных беременностью и увеличением веса. Беловин содержит специальные масла, такие как миндальное масло, экстракты оливкового масла и алоэ вера, чтобы сохранить кожу мягкой и эластичной. В составе этих масел содержится много линолевой и олеиновой кислот, витамины А, F,Е и В2, магний, фосфор, цинк, каротины и многие другие полезные вещества, питающие и укрепляющие кожу.</p>\r\n<p><strong>Форма выпуск</strong><span><strong>а</strong>: 30 г крем</span></p>', '', 'belovin-1566825367.jpg', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('51', '4', '0', '1566825394', 'ENOMOMAX', 'ENOMOMAX', '', '', '', '', '', '', '', '<p><strong>ENOMOMAX</strong> is a rectal device that has an irritating effect on the mucous membrane of the rectum and reflex stimulation of the intestinal motility, helping to exacerbate the mass of the stool instantly and without pain. Enomomax is used by men and women over 12 years of age and can be used twice a day if needed. It is not recommended to use more than seven days.</p>\r\n<p><strong>Release form</strong>: 6,75 gr glycerol rectal solution</p>', '<p><strong>ENOMOMAX</strong> - это слабительное средство, оказывающее легкое раздражающее воздействие на слизистую прямой кишки и рефлекторно стимулирующее двигательную активность кишечника, кроме этого, препарат смягчает каловые массы, что способствует их более быстрому и безболезненному выведению.<br />Enomomax используется мужчинами и женщинами старше 12 лет, при необходимости может применяться два раза в день. Использование клизмы более семи дней не рекомендуется.</p>\r\n<p><strong>Форма выпуска</strong>: 6,75 гр глицерина ректального раствора</p>', '', 'enomomax-1566826894.jpg', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('52', '14', '0', '1566826950', 'FEMIAUR', 'FEMIAUR', '', '', '', '', '', '', '', '<p><strong>FEMIAUR</strong> -Disposable vaginal douche has been specially designed for vaginal hygiene. It contains 10% povidone iodine as an active ingredient. It has antiseptic, antiviral and antibacterial properties. It is used for bacterial, chlamydial, ureaplasma and mycoplasma vaginitis. Femiaur, unlike other antiseptics, normalizes the acidity of the vaginal environment, which creates the conditions for the rapid restoration of the normal microflora of the vagina, which is an important factor in the absence of relapse of the vaginal infection after treatment. It has no side effects due to its active organic ingredients.</p>\r\n<p><strong>Release form</strong>: 5 ml x 10 ampoules</p>', '<p><strong>FEMIAUR </strong>-одноразовый вагинальный душ был специально разработан для гигиены влагалища. Он содержит 10% йода повидона в качестве активного ингредиента. Обладает антисептическими, противовирусными и антибактериальными свойствами. Применяется при бактериальной, хламидийной, уреаплазменной и микоплазменной вагинитах . Фемиаур в отличие от других антисептиков, нормализует кислотность вагинальной среды, чем создает условия для быстрого восстановления нормальной микрофлоры влагалища, что является важным фактором отсутствия рецидивов вагинальной инфекции после лечения. Он не проявляет побочных эффектов благодаря своим активным органическим ингредиентам.</p>\r\n<p><strong>Форма выпуска</strong>: 5 мл х 10 ампул</p>', '', 'femiaur-1566827951.jpg', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('53', '4', '0', '1566885560', 'RECTOMAX', 'RECTOMAX', '', '', '', '', '', '', '', '<p><strong>RECTOMAX</strong> is an enema for cleansing the intestines from feces. Rectal solution is suitable for adults and young people over 12 years old. Suitable for professional use before rectal surgery, after prolonged constipation, before colonoscopy, reduces rectal tension and soreness before surgery. Not applicable for daily use.</p>\r\n<p><strong>Release form</strong>: 118 ml laxative saline solution</p>', '<p><strong>RECTOMAX - </strong>это клизма для ичищения кишечника от каловых масс. Ректальный раствор подходит для взрослых и молодых людей старше 12 лет. Подходит для профессионального использования перед ректальными операциями, после длительного запора, перед колоноскопией, Уменьшает напряжение прямой кишки и болезненность перед операцией. Не применяется для ежедневного использования.</p>\r\n<p><strong>Форма выпуска</strong>: 118 мл слабительный солевой раствор</p>', '', 'rectomax-1566887518.jpg', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('54', '14', '0', '1566893274', 'AUR FRESH', 'AUR FRESH', '', '', '', '', '', '', '', '<p><strong>AUR FRESH</strong> intimate area daily cleanser has anti-inflammatory, antibacterial and antifungal, deodorizing effects. Aur Fresh provides hygiene of the external genital organs, refreshes and gives comfort throughout the day. With the help of chamomile extract Aur Fresh gently cleanses and moisturizes, reduces inflammation in the external genital area. Its special formula, adjusted to pH 4.5, is compatible with the natural structure of the pH of the genital area. The sensation of freshness, which begins immediately, continues throughout the day.</p>\r\n<p><strong>Release form</strong>: 275 ml daily cleaning liquid</p>', '<p>Ежедневное очищающее средством <strong>AUR FRESH</strong>&nbsp;для интимной зоны обладает противовоспалительным, антибактериальным и противогрибковым, дезодорирующими действиями. Aur Fresh обеспечивает гигиену наружных половых органов, освежает и дарит комфорт в течение дня. С помощью экстракта ромашки Aur Fresh мягко очищает и увлажняет , уменьшает воспаление в областе наружных половых органов. Его специальная формула, настроенная на pH 4,5, совместима с естественной структурой pH области гениталий. Ощущение свежести, которое начинается сразу же, продолжается в течение дня.</p>\r\n<p><strong>Форма выпуска</strong>: 275 мл ежедневной чистящей жидкости</p>', '', 'aur-freshshhsh-1566893934.jpg', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('55', '14', '0', '1566969766', 'AUR SOD', 'AUR SOD', '', '', '', '', '', '', '', '<p><strong>AUR SOD</strong> is a disposable vaginal shower with 0.025% carbonate and 2% sodium bicarbonate which can adjust the pH of the solution between 8-9. This helps in relieving symptoms of vulvar vaginitis. Usually, women suffer from symptoms of vulvovaginitis during menopause. Aur sod is widely used by both women of childbearing and women during menopause. Thanks to the alkaline solution, the shower reduces burning and dryness in the vagina. It has antiseptic, antiviral and antibacterial properties. Contains aloe vera which helps reduce burning sensation and itching.</p>\r\n<p><strong>Release form</strong>: 100 ml of vaginal soap</p>', '<p><strong>AUR SOD</strong>&nbsp;это одноразовый вагинальный душ с 0,025% -ым карбонатом и 2%-ым бикарбонат натрия&nbsp; которые могут регулировать рН раствора между 8-9. Это помогает в облегчении симптомов при вульве-вагинитах. Обычно симптомами вульвовагинита страдают женщины во время менопаузы. Aur sod широко используется как женщинами детородного, так и женщинами во время менопаузы. Благодаря щелочному раствору, душ уменьшает жжение и сухость во влагалище. Он обладает антисептическими, противовирусными и антибактериальными свойствами. Содержит алоэ веру который помогает уменьшить жжение и зуд.</p>\r\n<p><strong>Форма выпуска</strong>: 100 мл вагинального мыла</p>', '', 'aursod-1566970486.jpg', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('57', '10', '0', '1567759595', 'BABY MİX 5 (Apple flavored)', 'BABY MİX 5 (Cо вкусом яблока)', '', '', '', '', '', '', '', '<p><strong>BABY MIX 5</strong> has a calming and polluting, antispasmodic effect thanks to its unique formula with natural active and effective ingredients. Baby Mix 5 also contains the active ingredient fennel, which has an antispasmodic effect on the smooth muscles of the intestines, which supports digestion, and is therefore widely used for spastic colic and digestive disorders.</p>\r\n<p><strong>Release form</strong>: 150 ml syrup</p>', '<p><strong>BABY MIX</strong> <strong>5</strong> благодаря уникальной формуле с натуральными активными и эффективными ингредиентами, обладает успокоивающим и ветрогонным, спазмолитическими действиями. Baby Mix 5 содержит активный компонент фенхель, который также способствует перевариванию, обладая спазмолитическим действием, особенно в отношении гладкой мускулатуры кишечника и поэтому широко применяется при спастических коликах и при расстройствах пищеварения.</p>\r\n<p><strong>Форма выпуска</strong>: 150 мл сироп</p>', '', 'baby-mix-5-apple-1567759578.jpg', '0', '0', '0', '1');
INSERT INTO `products` VALUES ('58', '10', '0', '1567769483', 'BABY MIX 5 (Strawberry flavoured)', 'BABY MİX 5  (Со вкусом клубники)', '', '', '', '', '', '', '', '<p><strong>BABY MIX</strong><span> has a calming and polluting, antispasmodic effect thanks to its unique formula with natural active and effective ingredients. Baby Mix also contains the active ingredient fennel, which has an antispasmodic effect on the smooth muscles of the intestines, which supports digestion, and is therefore widely used for spastic colic and digestive disorders.</span></p>\r\n<p><span><strong>Release form</strong><span>: 150 ml syrup</span></span></p>', '<p><strong>BABY MIX </strong>благодаря уникальной формуле с натуральными активными и эффективными ингредиентами, обладает успокоивающим и ветрогонным, спазмолитическими действиями. Baby Mix содержит активный компонент фенхель, который также способствует перевариванию, обладая спазмолитическим действием, особенно в отношении гладкой мускулатуры кишечника и поэтому широко применяется при спастических коликах и при расстройствах пищеварения.</p>\r\n<p><strong>Форма выпуска:</strong> 150 мл сироп</p>', '', 'baby-mix-5-strawberry-1567769920.jpg', '0', '0', '0', '1');
